import React, { useState, useCallback } from 'react';
import s from './StepName.module.css';
import { NavLink } from 'react-router-dom';
import { Field, Form } from 'react-final-form';
import setFormData from '../services/setFormData';
import getFormData from '../services/getFormData';
import { STEPPER_FORM_DATA_KEY } from '../constants';

const StepName = () => {
    // sessionStorage.removeItem(STEPPER_FORM_DATA_KEY);
    const [initialValues] = useState(getFormData());

    const onSubmit = useCallback(
        (values) => {
            setFormData(values);
        }
    )
    const required = value => (value ? undefined : 'Required')
    return (
        <div className={s.StepName}>
           <div className={s.titleSpan}> <span >Оставьте заявку, и мы с вами свяжемся</span></div>
            <div className={s.secondTitleSpan}><span className={s.secondTitleSpan}>* Как к вам обращаться?</span></div>
            <Form
                onSubmit={onSubmit}
                initialValues={initialValues}
                render={({ handleSubmit, form, submitting, pristine, values }) => (
                    <form onChange={handleSubmit}>
                        <Field
                            className={s.field}
                            name='UserName'
                            component='input'
                            type='text'
                            lable='User Name'
                            variant='outlined'
                            placeholder='ваше имя'
                            validate={required}
                        />
                        {console.log(JSON.stringify(values, 0, 2))}
                    </form>
                )}

            />
            <NavLink to='/stepMessanger'><button className={s.buttonStepName} type='submit'>Далее</button></NavLink>
        </div>
    );
};
export default StepName;
