import React, { useState, useCallback } from 'react';
import s from './StepTel.module.css';
import { NavLink } from 'react-router-dom';
import {Field,Form} from 'react-final-form';
import setFormData from '../services/setFormData';
import getFormData from '../services/getFormData';
const StepTel = () => {
    const [initialValues] = useState(getFormData());

    const onSubmit = useCallback(
        (value)=>{
            setFormData(value);
        }
    );
    return (
        <div className={s.stepTel}>
           <div className={s.titleSpan}> <span >Оставьте заявку, и мы с вами свяжемся</span></div>
            <span>Укажите свой номер телефона</span>
            <Form
            onSubmit = {onSubmit}
            initialValues = {initialValues}
            render = {({handleSubmit, form, submitting, pristine, values})=>(
                <form onChange={handleSubmit}>
                    <Field
                    className = {s.field}
                    name='UserTelNumber'
                    component='input'
                    type='tel'
                    lable='User Tel Number'
                    variant='outlined'
                    />
                   {console.log(JSON.stringify(values, 0, 2))}
                </form>
            )}
            />
                  <NavLink to='/stepEmail'><button type='submit'>Далее</button></NavLink>
        </div>
    );
};
export default StepTel;
