import React, { useState, useCallback } from 'react';
import s from './StepMessanger.module.css';
import { NavLink } from 'react-router-dom';
import { Field, Form } from 'react-final-form';
import setFormData from '../services/setFormData';
import getFormData from '../services/getFormData';
import {STEPPER_FORM_DATA_KEY} from '../constants';

const StepMessanger = () => {
    const [initialValues] = useState(getFormData());

    const onSubmit = useCallback(
        (values) => {
            setFormData(values);
        }
    )
    return (
        <div>
            <span>Оставьте заявку, и мы с вами свяжемся</span><br />
            <span>* Мессенждер для связи</span>
            <Form
                onSubmit={onSubmit}
                initialValues={initialValues}
                render={({ handleSubmit,form, submitting, pristine, values }) => (
                    <form onSubmit={handleSubmit}>
                        <Field
                            className={s.field}
                            name='Messanger'
                            component='select'
                            lable='User Name'
                            variant='outlined'
                        >
                            <option >Мессенждер для связи</option>
                            <option value='telegram'>Telegram</option>
                            <option value="what's app">What's App</option>
                            {console.log(initialValues.Messanger)}
                            </Field>
                    </form>
                )}
            />
            <NavLink to='/stepTel'><button type='submit'>Далее</button></NavLink>

        </div>
    );
};
export default StepMessanger;
