import { React, useCallback, useState, useEffect } from 'react';
import s from './StepEmail.module.css';
import { Field, Form } from 'react-final-form';
import setFormData from '../services/setFormData';
import getFormData from '../services/getFormData';
import Email from '../../../images/leaveARequest/Email.png';
import Telegram from '../../../images/leaveARequest/Telegram.png';
import Whatsapp from '../../../images/leaveARequest/Whatsapp.png';
import Pattern from '../../../images/reviewsImage/Pattern.png';
import emailjs from 'emailjs-com';
import axios from 'axios'

const StepEmail = () => {

    const [initialvalues] = useState(getFormData());

    let state = getFormData();
    console.log(state)

    const onSubmit = useCallback(
        (values) => {
            setFormData(values)
        }
    );

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('MailCraft', 'template_2j66wqq', e.target, 'user_mfMT2tqhpUa5wrTjt0ztj')
      .then((result) => {
          console.log(e.target)
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  }

// let dataForms = {
//     service_id: 'service_9g2ao9l',
//     template_id: 'template_2j66wqq',
//     user_id: 'user_mfMT2tqhpUa5wrTjt0ztj',
//     template_params: {
//         'username': 'EmailCraftframe',
//         'g-recaptcha-response': '03AHJ_ASjnLA214KSNKFJAK12sfKASfehbmfd...'
//     }
// };




//  let sendEmail = event => {
//         event.preventDefault();

//       axios.post('https://api.emailjs.com/api/v1.0/email/send-form',{dataForms})
//       .then(res => {
//           console.log(res);
//           console.log(res.data);
//         })
//   }
    return (
        <div className={s.stemEmail}>
           <div className={s.titleSpan}> <span >Оставьте заявку, и мы с вами свяжемся</span></div>
           <div className={s.secondTitleSpan}><span className={s.secondTitleSpan}>*Последний шаг. Укажите вашу почту :)</span></div>
            <Form
            onSubmit ={onSubmit}
            initialValues = {initialvalues}
            render={({handleSubmit, form, submitting, pristine, values})=>(
                <form onChange={handleSubmit} onSubmit={sendEmail}> 
                    <Field
                    className = {s.field}
                    name='UserEmail'
                    component='input'
                    lable="User Email"
                    variant='outlined'

                    /><br/>
                    <Field
                    className={s.fieldButton}
                    name='ButtonEmail'
                    component='button'
                    lable='Button Email'
                    >Оставить заявку</Field>
                </form>
            )}
            ></Form>
            <div className={s.footerSpan}>Вы также можете написать нам</div>
       <div className={s.secondFooterSpan}>самостоятельно:</div>
       <div className={s.footerImg}>
           <a href='https://telegram.me/anna_dimitrak'><img src={Telegram}></img></a>
           <a href='mailto:craftframe.studio@gmail.com'><img src={Email}></img></a>
           <a href='https://wa.me/+79175850248'><img src={Whatsapp}></img></a>
       </div>
       <div className={s.footerPattern}><img src={Pattern}/></div>

       <form onSubmit={sendEmail}>
           <input defaultValue={getFormData().UserName} name='UserName'></input>
           <input defaultValue={getFormData().UserName} name='UserTelNumber'></input>
           <input defaultValue={getFormData().UserName} name='Messanger'></input>
           <input name='UserEmail'></input>
           <button type='submit'>seeend</button>


       </form>
        </div>
    );
};
export default StepEmail;
