const defaultState = {
    cash:10

  }

 export const addUserNameReducer = (state = defaultState, action) => {
    switch (action.type) {
  case 'ADD_NAME':
  return {...state, cash: state.cash + action.payload}
  case 'GET_NAM':
    return {...state, cash: state.cash - action.payload}

      default:
        return state
    }
  }
