import React, { useState, useCallback } from 'react';
import s from './StepTel.module.css';
import { NavLink } from 'react-router-dom';
import {Field,Form} from 'react-final-form';
import setFormData from '../services/setFormData';
import getFormData from '../services/getFormData';
const StepTel = () => {
    const [initialValues] = useState(getFormData());

    const onSubmit = useCallback(
        (value)=>{
            setFormData(value);
        }
    );
    return (
        <div>
            <span>Оставьте заявку, и мы с вами свяжемся</span><br/>
            <span>Укажите свой номер телефона</span>
            <Form
            onSubmit = {onSubmit}
            initialValues = {initialValues}
            render = {({handleSubmit})=>(
                <form onSubmit={handleSubmit}>
                    <Field
                    className = {s.field}
                    name='UserTelNumber'
                    component='input'
                    type='tel'
                    lable='User Tel Number'
                    variant='outlined'
                    />
                </form>
            )}
            />
            <NavLink to='/StepTel'><button type='submit'>Далее</button></NavLink>
            <div>
                {Object.entries(initialValues).map(([label,value])=>{
                    return(
                        <p key={label}>
                            <b>{label}</b>
                        </p>
                    )
                })}
            </div>
        </div>
    );
};
export default StepTel;
