import { React, useCallback, useState } from 'react';
import s from './StepEmail.module.css';
import { Field, Form } from 'react-final-form';
import setFormData from '../services/setFormData';
import getFormData from '../services/getFormData';


const StepEmail = () => {

    const [initialvalues] = useState(getFormData());

    const onSubmit = useCallback(
        (values) => {
            setFormData(values)
        }
    )
    return (
        <div className={s.stemEmail}>
           <div className={s.titleSpan}> <span >Оставьте заявку, и мы с вами свяжемся</span></div>
            <span>*Последний шаг. Укажите вашу почту :)</span><br/>
            <Form
            onSubmit ={onSubmit}
            initialValues = {initialvalues}
            render={({handleSubmit, form, submitting, pristine, values})=>(
                <form onChange={handleSubmit}>
                    <Field
                    className = {s.field}
                    name='UserEmail'
                    component='input'
                    lable="User Email"
                    variant='outlined'
                    /><br/>
                    <Field
                    className={s.fieldButton}
                    name='ButtonEmail'
                    component='button'
                    lable='Button Email'
                    >Оставить заявку</Field>
                    {console.log(JSON.stringify(values,0,2))}
                </form>
            )}

            ></Form>
        </div>
    );
};
export default StepEmail;
