import React, { useState, useCallback } from 'react';
import s from './StepName.module.css';
import { NavLink } from 'react-router-dom';
import { Field, Form } from 'react-final-form';
import setFormData from '../services/setFormData';
import getFormData from '../services/getFormData';
import { STEPPER_FORM_DATA_KEY } from '../constants';

const StepName = () => {
    // sessionStorage.removeItem(STEPPER_FORM_DATA_KEY);
    const [initialValues] = useState(getFormData());

    const onSubmit = useCallback(
        (values) => {
            setFormData(values);
        }
    )
    return (
        <div>
            <span>Оставьте заявку, и мы с вами свяжемся</span><br />
            <span>* Как к вам обращаться?</span>
            <Form
                onSubmit={onSubmit}
                initialValues={initialValues}
                render={({ handleSubmit, form, submitting, pristine, values }) => (
                    <form onSubmit={handleSubmit}>
                        <Field
                            className={s.field}
                            name='UserName'
                            component='input'
                            type='text'
                            lable='User Name'
                            variant='outlined'
                        />
                        <Field
                            name='send'
                            component='button'
                            type='submit'>
                     hjhkjhkjh </Field>
                        {console.log(JSON.stringify(values, 0, 2))}
                        <button type='submit'>mkmlkmkms</button>
                        <NavLink  to='/stepMessanger'>Далее</NavLink>
                    </form>
                )}

            />
            {/* <NavLink to='/stepMessanger'><button type='submit'>Далее</button></NavLink> */}
        </div>
    );
};
export default StepName;
